tinymce.PluginManager.add('jbimages',function(editor,url){function jbBox(){editor.windowManager.open({title:'Enviar uma imagem',file:url+'/dialog-v4.htm',width:350,height:135,buttons:[{text:'Enviar',classes:'widget btn primary first abs-layout-item',disabled:true,onclick:'close'},{text:'Close',onclick:'close'}]})}editor.addButton('jbimages',{tooltip:'Enviar uma imagem',icon:'image',text:'Imagem',onclick:jbBox});editor.addMenuItem('jbimages',{text:'Imagem',icon:'image',context:'insert',onclick:jbBox})});